package kot;

public class Ternary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,k;
		i=10;
		k = i<0 ? -i : i;
		System.out.println(i+" absolute value ="+k);
		i=-10;
		k = i<0 ? -i : i;
		System.out.println(i+" absolute value ="+k);
	}

}
